def calculate_purity_per_cluster(clusters, label_dict, accumulated_docs=None):
    purity_scores = {}
    total_documents = 0
    correct_assignments = 0

    # Tambahkan dokumen dari iterasi sebelumnya jika ada
    if accumulated_docs is None:
        accumulated_docs = set()

    for term_set, documents in clusters.items():
        label_count = {}
        for doc_id in documents:
            # Tambahkan dokumen ke set yang terakumulasi
            accumulated_docs.add(doc_id)
            label = label_dict.get(doc_id, None)
            if label:
                if label in label_count:
                    label_count[label] += 1
                else:
                    label_count[label] = 1

        if label_count:
            max_label = max(label_count, key=label_count.get)
            correct = label_count[max_label]
            purity = correct / len(documents)
            purity_scores[term_set] = purity

            total_documents += len(documents)
            correct_assignments += correct

    overall_purity = correct_assignments / total_documents if total_documents > 0 else 0
    return purity_scores, overall_purity, accumulated_docs

def calculate_purity_for_all_iterations(clustering_results, label_dict):
    overall_purity_scores = {}
    accumulated_docs = set()  # Set untuk menyimpan dokumen yang sudah diproses

    for iteration, clusters in clustering_results.items():
        purity_scores, overall_purity, accumulated_docs = calculate_purity_per_cluster(clusters, label_dict, accumulated_docs)
        overall_purity_scores[iteration] = overall_purity
        print(f"Purity per Cluster for {iteration}:")
        for term_set, purity in purity_scores.items():
            print(f"  {term_set}: {purity:.2f}")
        print(f"Overall Purity for {iteration}: {overall_purity:.2f}\n")

    return overall_purity_scores

# run function purity
purity_scores = calculate_purity_for_all_iterations(clustering_results, label_dict)